import React, { useState, useEffect } from "react";
import ListElement from "../../components/ListElement";

const NoteList = ({ triggerShowStorage }) => {
	const [dataList, setDataList] = useState();

	// 	useEffect(() => {
	// 		let notes = [];
	// 		for (let i = 0; i < localStorage.length; i++) {
	// 			notes.push(JSON.parse(localStorage.getItem(i)));
	// 		}
	// 		setDataList(notes);
	//     }, []);

	useEffect(() => {
		var allnotekeys = [];
		for (let i = 0; i <= localStorage.length; i++) {
			console.log("Show keys " + localStorage.key(i));
			let xx = allnotekeys.push(localStorage.key(i));
			// let notekeys = JSON.parse(localStorage.getItem(localStorage.key(i)));
			console.log("Keys list " + allnotekeys);
			// let allnotes = allnotekeys.map()
			let notes = allnotekeys.map((data) => <ListElement Note={data.note} />);
		}
	}, []);

	return (
		<div>
			<p style={{ marginLeft: "10px" }}> List of notes: {notes}</p>
		</div>
	);
};

export default NoteList;
