import React, { useState, useEffect } from "react";

const NoteList = ({ triggerShowStorage }) => {
	const [dataList, setDataList] = useState();

	useEffect(() => {
		let notes = [];
		for (let i = 0; i < localStorage.length; i++) {
			notes.push(JSON.parse(localStorage.getItem(i)));
		}
		setDataList(notes);
	}, []);

	// const updateMyData = (data) => {
	// 	setDataList(data);
	// };

	const notesListRender = notes.map((data, index) => (
		<div className="notes-list" key={index}>
			<li>
				<b>{data.title ? data.title : "Sans titre"}</b> :{" "}
				{data.text ? data.text.substring(0, 15) : "(vide)"}
			</li>
		</div>
	));

	return (
		<div className="notes-list">
			<p>{notesListRender} </p>
		</div>
	);
};

