import React from "react";
import Showdown from "showdown";

const NoteDisplay = ({ note, title }) => {
	const converter = new Showdown.Converter();
	var htmlTitle = converter.makeHtml(title);
	var htmlNote = converter.makeHtml(note);
	function createMarkupNote() {
		return {
			__html: htmlTitle,
		};
	}

	function createMarkupTitle() {
		return {
			__html: htmlTitle,
		};
	}

	return (
		<div>
			<p style={{ marginLeft: "10px" }}> Your better notes</p>
			<p style={{ marginLeft: "10px" }}> Title: {htmlTitle} </p>
			<p style={{ marginLeft: "10px" }}> Note:{htmlNote} </p>
			<div dangerouslySetInnerHTML={createMarkupTitle()} />
			<div dangerouslySetInnerHTML={createMarkupNote()} />
		</div>
	);
};

export default NoteDisplay;
