import React, { useState, useEffect } from "react";

const NoteDisplay = ({ triggerShowStorage }) => {
	// const getAllItems = () => {
	// 	var allnotekeys = [];
	// 	for (let i = 0; i <= localStorage.length; i++) {
	// 		console.log("Show keys " + localStorage.key(i));
	// 		let xx = allnotekeys.push(localStorage.key(i));
	// 		// let notekeys = JSON.parse(localStorage.getItem(localStorage.key(i)));
	// 		console.log("Keys list " + allnotekeys);
	// 		// let allnotes = allnotekeys.map()
	// 	}
	// };

	const NotesList = ({ triggerShowStorage }) => {
		const [note, setNote] = useState({});
		let notes = [];
		for (let i = 0; i < localStorage.length; i++) {
			notes.push(localStorage.key(i));
		}

		useEffect(() => {
			triggerShowStorage(note);
		}, [note]);

		const getStorageItem = (n) => {
			setNote({ title: n, note: JSON.parse(localStorage.getItem(n)) });
		};

		const notesListRender = notes.map((n, id) => (
			<li key={id} className="list-group-item">
				<a href="#" onClick={() => getStorageItem(n)}>
					{n} - {JSON.parse(localStorage.getItem(n)).slice(0, 15)}
				</a>
			</li>
		));

		return (
			<div className="col-3">
				<ul className="list-group mt-5">{notesListRender}</ul>
			</div>
		);
	};

	// return (
	// 	<div style={{ marginLeft: "10px", marginBottom: "30px" }}>List of notes:</div>
	// );
};

export default NoteDisplay;
