import React, { useState, useEffect } from "react";
import NoteList from "../../components/NoteList";

const MarkdownInput = ({ triggerSetNote, note }) => {
	const [input, setInput] = useState({});

	const handleInputChange = (e) =>
		setInput({
			...input,
			[e.currentTarget.name]: e.currentTarget.value,
		});

	const handleClick = (e) => {
		e.preventDefault();
		triggerSetNote(input);
		localStorage.setItem(input.note, JSON.stringify(input));
		getAllItems();
	};

	const getAllItems = () => {
		var allnotekeys = [];
		for (let i = 0; i <= localStorage.length; i++) {
			console.log("Show keys " + localStorage.key(i));
			allnotekeys.push(localStorage.key(i));
			// let notekeys = JSON.parse(localStorage.getItem(localStorage.key(i)));
			console.log("Keys list " + allnotekeys);
			// let allnotes = allnotekeys.map()
		}
	};

	// useEffect(() => {
	// let notekeys = JSON.parse(localStorage.getItem(localStorage.key(i)));
	//   }, []);

	// const getKeys = allnotekeys.map((item) => <p>{item}</p>);

	// 	const dispayNotes = notes.map ((item, key) =>
	//     <li key={item.id}>{item.name}</li>
	// );

	// const notesListRender = notes.map((n, id) => (
	//     <li key={id} className="list-group-item">
	//       <a href="#" onClick={() => getStorageItem(n)}>
	//         {n} - {JSON.parse(localStorage.getItem(n)).slice(0, 15)}
	//       </a>
	//     </li>
	//   ));

	return (
		<div>
			<NoteList triggerShowStorage={(input) => setInput(input)} />
			<form>
				<label>
					<textarea
						type="text"
						name="notetitle"
						onChange={handleInputChange}
						rows="1"
						cols="40"
						style={{ marginLeft: "10px" }}
						placeholder="Note title..."
						required
					/>
					<br></br>
					<textarea
						type="text"
						name="note"
						onChange={handleInputChange}
						rows="4"
						cols="50"
						style={{ marginLeft: "10px" }}
						placeholder="Write your bloc note..."
						required
					/>
					<br />
					<input
						className="btn btn-dark"
						type="submit"
						value="Save"
						style={{ marginLeft: "10px" }}
						onClick={handleClick}
					></input>
				</label>
			</form>
		</div>
	);
};

export default MarkdownInput;
