import React, { useState, useEffect } from "react";

const NoteDisplay = ({ triggerShowStorage }) => {
	// const getAllItems = () => {
	// 	var allnotekeys = [];
	// 	for (let i = 0; i <= localStorage.length; i++) {
	// 		console.log("Show keys " + localStorage.key(i));
	// 		let xx = allnotekeys.push(localStorage.key(i));
	// 		// let notekeys = JSON.parse(localStorage.getItem(localStorage.key(i)));
	// 		console.log("Keys list " + allnotekeys);
	// 		// let allnotes = allnotekeys.map()
	// 	}
	// };

	const [dataList, setDataList] = useState({});

	useEffect(() => {
		let notes = [];
		for (let i = 0; i < localStorage.length; i++) {
			notes.push(JSON.parse(localStorage.getItem(i)));
		}
		updateMyData(notes);
	}, []);

	const updateMyData = (data) => {
		setDataList(data);
	};

	const renderedItem = (
		<div>
			<ListDisplay noteList={dataList} />
		</div>
	);

	return (
		<>
			<h3>notes</h3>
			<div>
				{dataList && dataList.length > 0 ? renderedItem : <p>Aucune note</p>}
			</div>
		</>
	);
};

export default NoteDisplay;

const ListDisplay = ({ noteList }) => {
	return (
		<ul id="container">
			{noteList.map((data, index) => (
				<div className="notes-list" key={index}>
					<li>
						{data} {data.title}
					</li>
				</div>
			))}
		</ul>
	);
};
